function ready(){$("ul.scorecard-tabs li:first a:first").tab("show"),$(".rating-slider").slider({tooltip:"hide",formatter:function(t){return"Flavor rating: "+t}}),$(".rating-slider").on("slide",function(t){var r=$(t.target),a=r.closest(".row").find("strong.rating-value-holder");a.text(r.data("rating-attr")+" - "+t.value)})}$(document).ready(ready),$(document).on("page:load",ready);